<?phpfunction get_page($var_name, $default_value) {    if (isset($_GET[$var_name])) {        return $_GET[$var_name];    } else {        return $default_value;    }}function startsWith($haystack, $needle) {    return !strncmp($haystack, $needle, strlen($needle));}function get_url_option($option_name, $default_value) {    if (isset($_GET[$option_name])) {        $value = $_GET[$option_name];    } else {        $value = $default_value;    }    return $value;}function add_strong($text) {    return '<strong>' . $text . '</strong>';}function add_em($text) {    return '<em>' . $text . '</em>';}?>